import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation, Navigate } from "react-router-dom";
import MainNavbar from "./components/MainNavbar";
import AppNavbar from "./components/AppNavbar";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Profile from "./pages/Profile";
import NotFound from "./pages/NotFound";
import MainPage from "./pages/MainPage";
import JournalPage from "./pages/JournalPage";
import ChatPage from "./pages/ChatPage";
import { useAuth } from "./contexts/AuthContext";
import About from './pages/About';
import Contact from './pages/Contact';

const Layout = ({ children }) => {
  const location = useLocation();
  const { user, logout } = useAuth();
  const showMainNavbar = ["/", "/login", "/register", "/about", "/contact"].includes(location.pathname);

  return (
    <>
      {showMainNavbar ? <MainNavbar /> : <AppNavbar username={user?.username} onLogout={logout} />}
      {children}
    </>
  );
};

function App() {
  const { user, loading } = useAuth();
  const [theme, setTheme] = useState('light');

  useEffect(() => {
    document.body.setAttribute('data-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  if (loading) {
    return <div>Cargando...</div>;
  }

  return (
    <>
      {/* Bot√≥n de modo oscuro/claro, siempre visible arriba a la derecha */}
      <button
        onClick={toggleTheme}
        style={{
          position: "fixed",
          top: 10,
          right: 10,
          zIndex: 1000,
          padding: "0.5rem 1rem",
          borderRadius: "20px",
          border: "1px solid #888",
          background: "var(--background)",
          color: "var(--text)",
          cursor: "pointer"
        }}
      >
        Cambiar a {theme === 'light' ? 'Modo Oscuro' : 'Modo Claro'}
      </button>
      <Router>
        <Layout>
          <Routes>
            {/* No login accessible pages */}
            <Route path="/" element={user ? <Navigate to="/journal" replace /> : <MainPage />} />
            <Route path="/login" element={user ? <Navigate to="/journal" replace /> : <Login />} />
            <Route path="/register" element={user ? <Navigate to="/journal" replace /> : <Register />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />

            {/* Login-only accessible pages */}
            <Route
              path="/chat"
              element={user ? <ChatPage /> : <Navigate to="/login" replace />}
            />
            <Route
              path="/journal"
              element={user ? <JournalPage /> : <Navigate to="/login" replace />}
            />
            <Route
              path="/profile"
              element={user ? <Profile /> : <Navigate to="/login" replace />}
            />

            {/* Not found page */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </Layout>
      </Router>
    </>
  );
}

export default App;
